import React, { Component } from 'react'
import './app.css';
import Search from './SearchBar/Search';
import axios from 'axios';
import ImagaList from './image/ImagaList';
class App extends Component {
   state={
    images:[]
   }
  onSearchImage = async(search) => {

    const result = await axios.get('https://api.unsplash.com/photos', {
      params: {
        query:search
      },
      headers: {
        Authorization:'Client-ID 7eZLBNPZEtl0nialAJQgzbKTMIuqrdtZZK9jGkie2hM'
      }
    })
    this.setState({
      images:result.data.results
    })
  }

  render() {
    return (
      <div className='app-container'>
        <Search onSearchImage={this.onSearchImage} />
         <ImagaList images={this.state.images}/>
      </div>
    )
  }
}
export default App;
